$(document).ready(function () {
	var didScroll = false;
	var notification_obj = $('.instant_notifications');
	var clipped_notifications = false;

	function isScrolledIntoView(elem) {
		var docViewTop = $(window).scrollTop();
		var docViewBottom = docViewTop + $(window).height();

		var elemTop = $(elem).offset().top;
		var elemBottom = elemTop + $(elem).height();

		return ((elemBottom >= docViewTop) && (elemTop <= docViewBottom) && (elemBottom <= docViewBottom) && (elemTop >= docViewTop));
	}

	var scroll_check = setInterval(function () {
			if (isScrolledIntoView('#signout_box')) {
				if (clipped_notifications) {
					$('.instant_notifications').css({
						position : 'inherit',
						top : 0
					});
					clipped_notifications = false;
				}
			} else {
				if (!clipped_notifications) {
					clipped_notifications = true;
					$('.instant_notifications').css({
						position : 'fixed',
						top : 0
					});
				}
			}
		}, 500);

	$('button[data-loading-text]').on('click', function () {
		$(this).button('loading').disable();
	});

	$('.magicTip').tooltip();
	$(".auto_tab").tabs("div.tab_content > div", {
		history : true
	});

	if ($('body').hasClass('no_sidebar')) {
		var new_logo = $('#logo').clone().addClass('sideless_logo');
		$('#content_header').prepend(new_logo);
		new_logo.fadeIn(300);
	}

	var search_query = false;
	var search_timeout = false;
	var search_interact = false;

	$('#main_search').bind('focus keydown', function () {
		var category = $('#search_type').val();
		var search_obj = $(this);
		if (search_obj.val().length > 1) {
			if (!search_query) {
				search_obj.addClass('scanning');
				$('#search_results').fadeIn(200);
				clearTimeout(search_timeout);
				search_timeout = setTimeout(function () {
						search_query = $.ajax({
								type : "POST",
								url : "/search/get_items/" + encodeURIComponent(search_obj.val()) + "/" + encodeURIComponent(category),
								cache : false,
								async : true,
								dataType : "json",
								success : function (json) {
									search_obj.removeClass('scanning');
									search_query = false;
									var i = json.results.length;
									console.log(i);
									var full_li_html = '';
									while (i--) {
										var tmp_html = '';
										tmp_html += '<li>';
										tmp_html += '<img src="/images/items/' + json.results[i].thumb + '" alt="" class="search_list_thumbnail" />';
										tmp_html += '<div class="search_list_response">';
										tmp_html += '<strong>' + json.results[i].name + '</strong>';
										tmp_html += '<span>';
										// if (json.results[i]['marketplace_item_id']) tmp_html += '<a href="/market/view_item/'+json.results[i]['item_id']+'">Find in market</a> &#8901;';
										// if (json.results[i]['shop_item_id']) tmp_html += '<a href="/shops/item/view/'+json.results[i]['shop_item_id']+'">View in shop</a> &#8901; ';
										// tmp_html += '<a href="/wishlist/add_item/'+json.results[i]['item_id']+'">Add to wishlist</a>';
										tmp_html += '</span>';
										tmp_html += '</div>';
										tmp_html += '</li>';
										full_li_html += tmp_html;
									}

									$('#search_results ul').html(full_li_html);
								}
							});
					}, 250);
			}
		} else {
			search_obj.removeClass('scanning');
			$('#search_results').fadeOut(200);
		}
	});

	$('#search_results > a').mousedown(function () {
		search_interact = true;
	});

	$('#search_type').mousedown(function () {
		search_interact = true;
	});

	$('#search_type').change(function () {
		$(this).blur();
		$('#main_search').focus();
		search_interact = false;
	});

	$('#main_search').bind('blur', function () {
		if (search_interact)
			return false;
		$(this).removeClass('scanning');
		$('#search_results').fadeOut(200);
	});

	var friend_array = [];
	$('input[data-suggest-friends]').focus(function () {
		if (friend_array.length === 0) {
			$.getJSON('/friends/get_friends/', function (json) {
				$('#new_trade_username').typeahead({
					source : json,
					items : 5
				});
			});
		}
	});

	$('.reveal_spoiler').live('click', function () {
		var handler = $(this);
		var arrow = handler.parent().find('.reveal_arrow');
		handler.parent().find('.spoiler_value').slideToggle();
		if (arrow.text() == "↑") {
			arrow.html('&darr;');
		} else {
			arrow.html('&uarr;');
		}
		return false;
	});
	
	$('.jump_to').live('click', function() {
		if (!$('#jumpToBox').is(":visible"))
			$('#jumpToBox')
				.css({
					top: $(this).offset().top + $(this).height(),
					left: ($(this).offset().left + ($(this).width() / 2))  - ($('#jumpToBox').width() / 2)
				})
				.fadeIn('fast');
		return false;
	});
	
	$('#jumpToBox a').live('click', function() {
		if (!$('#jumpToInput').val() || $('#jumpToInput').val() <= 0)
			$('#jumpToInput').val(1);

		if ($('#jumpToInput').val() > $('.jump_to:first').data('maxpage'))
			$('#jumpToInput').val($('.jump_to:first').data('maxpage'));

		window.location.href = $('.jump_to:first').data('target') + (($('#jumpToInput').val() - 1) * 10);
		return false;
	});
	
	$(document).mouseup(function (e) {
		var container = $('#jumpToBox');

		if (!container.is(e.target) && container.has(e.target).length === 0) {
			container.fadeOut('fast');
		}
	});
	
	$('.numbersOnly').keyup(function () {
		if (this.value != this.value.replace(/[^0-9\.]/g, '')) {
		   this.value = this.value.replace(/[^0-9\.]/g, '');
		}
	});
	
	var maxX = $(document).width() - $('#eventGrab').width(),
		maxY = $(document).height() - $('#eventGrab').height()

	
	$('#eventGrab').css({
		top: getRandomPosition(0, maxY),
		left: getRandomPosition(0, maxX)
	}).fadeIn();
});

function getRandomPosition(min, max) {
    return Math.random() * (max - min) + min;
}